import React,{useState} from "react";
import {Button} from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';

function Login(props){
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return(
        <div>
             <Button variant="outline-primary" onClick={handleShow}>
      Login
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Streaming Service</Modal.Title>
        </Modal.Header>
        
        <Modal.Body className='center'>

            <Form onSubmit={props.onSubmit}>
                <Form.Group controlId="formBasicEmail" className="mb-0 mt">
                    
                    <Form.Control type="email" className="w-50" name="username" onChange={props.onChange} placeholder="Username" required/>
                </Form.Group>

                <Form.Group controlId="formBasicPassword" className="mb-0">
                    <Form.Control type="password" className="w-50" name="password" onChange={props.onChange} placeholder="Password" required />
                </Form.Group>
                <Form.Group controlId="formBasicCheckbox" className="mt-5px" name="rememberMe" value="rememberMe">
                    <Form.Check type="checkbox" label="Remember Me"  />
                </Form.Group>
                <Button variant="outline-primary" type="submit">
                    Login
                </Button>
            </Form>
                
        </Modal.Body>
        <Modal.Footer>
        <div >
                <small>&copy; Copyright Streaming Service</small>
               
              </div>
        </Modal.Footer>
      </Modal>
        </div>
    );
}

export default Login;
