import React from "react";
import Navbar from "../component/navbar";
import Jumbotron from "../component/jumbotron";
import Login from "../component/login";
import CreateProfile from "../component/createprofile";
import {Route,Switch,useHistory} from "react-router-dom";
import Plan from "../component/plan";
import Footer from "../component/footer";
import Payment from "../component/payment";


function Signup(props){

   let history=useHistory(); 
    const [profile,setProfile]=React.useState({
        fname:"",
        lname:"",
        email:"",
        password:"",
        Cpassword:"",
        mobile:"",
        dob:""
        

    });
    const [planValue,setPlan]=React.useState({
       plan:"0"
    });

    const [validity,setValidity]=React.useState("");

    function handlePlan(event){
        var {name,value}=event.target;
        setProfile(()=>{
            return{
                [name]:value
            }  
        });  
    }
    
     function handleProfile(event){
        var {name,value}=event.target;
        
        setProfile( prevValue=>{
             return{
                 ...prevValue,
                [name]:value
            }  
        },()=>(console.log("hello")
        ));

         if(profile.Cpassword){
            if(profile.Cpassword===profile.password){
                setValidity("is-valid");
            }
            else{
                setValidity("is-invalid"); 
            }
        }
        
        
    }
    function submitProfile(event){
       event.preventDefault();
        console.log(profile);
       history.push("/signup/plan");    
    }

    function submitValue(event){
        event.preventDefault();
        console.log(profile);
    }

    return(
        <div>
            <Navbar>
                <Login />
            </Navbar>
            <Jumbotron>
                <Switch>
                <Route exact path="/signup">
                <CreateProfile onChange={handleProfile} valid={validity} onSubmit={submitProfile}  />
                </Route>
                <Route exact path="/signup/plan">
                    <Plan onChange={handlePlan}  onSubmit={submitValue} />
                </Route>
                </Switch>
                <Footer/>
            </Jumbotron>
        </div>
    );
}

export default Signup;
