import React,{useState} from "react";
import {Button} from "react-bootstrap";
import {connect} from "react-redux";
import axios from "axios";
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import {setCurrentUser} from "../redux/user/user_action";

function Login(props){
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const {setCurrentUser}=props;
    const[login,setStateLogin]=React.useState({
        username:"",
        password:""
    })

    function handleChange(event){
        var {name,value}=event.target;
        setStateLogin(prevValue=>{
            return{
                ...prevValue,
                [name]:value
            }  
        });
    }

   

    function handleSubmit(event){
        setCurrentUser(login);
        axios.get("http://localhost:3500/login/",{headers: {
            'Access-Control-Allow-Origin': "http://localhost:3500/",
            "Access-Control-Allow-Methods": POST,
            "Access-Control-Allow-Headers": Content-Type, Authorization},
            crossDomain: true
          })
          .then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            console.log(error);
          });
        event.preventDefault();
        
    }

    return(
        <div>
             <Button variant="outline-primary" onClick={handleShow}>
      Login
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Streaming Service</Modal.Title>
        </Modal.Header>
        
        <Modal.Body className='center'>

            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicEmail" className="mb-0 mt">
                    
                    <Form.Control type="email" className="w-50" name="username" onChange={handleChange} placeholder="Username" required/>
                </Form.Group>

                <Form.Group controlId="formBasicPassword" className="mb-0">
                    <Form.Control type="password" className="w-50" name="password" onChange={handleChange} placeholder="Password" required />
                </Form.Group>
                <Form.Group controlId="formBasicCheckbox" className="mt-5px" name="rememberMe" value="rememberMe">
                    <Form.Check type="checkbox" label="Remember Me"  />
                </Form.Group>
                <Button variant="outline-primary" type="submit">
                    Login
                </Button>
            </Form>
                
        </Modal.Body>
        <Modal.Footer>
        <div >
                <small>&copy; Copyright Streaming Service</small>
               
              </div>
        </Modal.Footer>
      </Modal>
        </div>
    );
}

const mapDispatchToprops=dispatch=>({
    setCurrentUser:user=>dispatch(setCurrentUser(user))
});

export default connect(
    null,
    mapDispatchToprops
)(Login);
